jQuery(function($) {
	
    var nonce = window._gstm_sort_group.nonce;
    window._gstm_sort_group = null;

	var resourceSort = $('#group-sortable-list');

	resourceSort.sortable({

		update: function() {

			$('#loading-animation').show(); // Show the animate loading gif while waiting

			$.ajax({
				url: ajaxurl, // ajaxurl is defined by WordPress and points to /wp-admin/admin-ajax.php
				type: 'POST',
				async: true,
				cache: false,
				dataType: 'json',
				data: {
					_nonce: nonce,
					action: 'update_taxonomy_order', // Tell WordPress how to handle this ajax request
					order: resourceSort.sortable('toArray').toString() // Passes ID's of list items in	1,3,2 format
				},
				success: function(response) {
					$('#loading-animation').hide(); // Hide the loading animation
					return; 
				},
				error: function(xhr,textStatus,e) {  // This can be expanded to provide more information
					alert('There was an error saving the updates');
					$('#loading-animation').hide(); // Hide the loading animation
					return; 
				}
			});

		}
		
	});

});
