(function($) {

    var GS_Testimonials = function( $scope, $ ) {

        var $tmWidget = $scope.find('.gs_testimonial_container');

        if ( ! $tmWidget.length ) return;

        $(document).trigger( 'gstm:scripts:reprocess' );

    }

    $(window).on( 'elementor/frontend/init', function() {

        elementorFrontend.hooks.addAction( 'frontend/element_ready/gs-testimonial.default', GS_Testimonials );

    });

})(jQuery);